document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll(".js-multi-button:not(.--initialized)").forEach((function(t){t.querySelector(".js-multi-button-trigger").addEventListener("click",(function(){t.classList.toggle("--active")})),document.addEventListener("click",(function(e){e.target&&!e.target.closest(".js-multi-button-trigger")&&t.classList.remove("--active")})),document.addEventListener("keydown",(function(e){"Escape"===e.key&&t.classList.remove("--active")}))}))}));