document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll(".js-numbers:not(.--initialized)").forEach((function(t){var e=t.querySelectorAll(".js-numbers-card-animation");window.pts.gsap.from(e,{scrollTrigger:t,textContent:0,duration:2,ease:"ease.out",snap:{textContent:1},stagger:{each:1,onUpdate:function(){var t=this.targets()[0];t.textContent=Number(t.textContent).toLocaleString()}}}),t.classList.add("--initialized")}))}));